DROP FUNCTION IF EXISTS levenshtein;

DELIMITER $$

CREATE FUNCTION levenshtein(s1 VARCHAR(255), s2 VARCHAR(255)) RETURNS INT DETERMINISTIC
BEGIN
  DECLARE s1_len, s2_len, i, j, c, c_temp, cost INT;
  DECLARE s1_char CHAR;
  DECLARE cv0, cv1 VARBINARY(256);

  SET s1_len = CHAR_LENGTH(s1), s2_len = CHAR_LENGTH(s2);
  IF s1_len = 0 THEN RETURN s2_len; END IF;
  IF s2_len = 0 THEN RETURN s1_len; END IF;
  IF s1_len > 255 OR s2_len > 255 THEN RETURN NULL; END IF;

  SET cv1 = ;
  SET cv0 = cv1;
  
  SET i = 1;
  WHILE i <= s1_len DO
    SET cv0 = INSERT(cv0, i, 1, i);
    SET i = i + 1;
  END WHILE;

  SET i = 1;
  WHILE i <= s1_len DO
    SET s1_char = SUBSTRING(s1, i, 1);
    SET c = i;
    SET cv1 = INSERT(cv1, 1, 1, i);
    SET j = 1;

    WHILE j <= s2_len DO
      SET cost = IF(s1_char = SUBSTRING(s2, j, 1), 0, 1);
      SET c_temp = INSERT(cv1, j + 1, 1, LEAST(c + 1, ORD(SUBSTRING(cv0, j + 1, 1)) + 1, c + cost));
      SET c = ORD(SUBSTRING(cv0, j + 1, 1));
      SET cv1 = c_temp;
      SET j = j + 1;
    END WHILE;

    SET cv0 = cv1;
    SET i = i + 1;
  END WHILE;

  RETURN ORD(SUBSTRING(cv1, s2_len + 1, 1));
END$$

DELIMITER ;
